import React from 'react'

const ContactForm = () => {

    const [contact,setContact]=useState({
        name:'',email:'',phone:'',type:''
    })
    return (
        <form>
            <h2 className='text-primary'> Add Contact
            </h2>
            <input type='text' name='name' value='name' placeholder='Name'/>
            <input type='text' name='email' value='email' placeholder='Email'/>
            <input type='text' name='phone' value='phone' placeholder='Phone'/>
            <h2>Contact Type</h2>
            <input type='radio' name='radio' value='personal' checked={type==='personal'}/>Personal
            <input type='radio' name='radio' value='professional' checked={type==='professional'}/>Professional
        </form>
    )
}

export default ContactForm
